import * as constans from './constants';
import { fromJS } from 'immutable';

const defaulState = fromJS( {
    focused: false
});

export default (state = defaulState, action) => {
if (action.type === constans.SEARCH_FOCUS) {
    return {
        focused: true
    }
}
if (action.type === constans.SEARCH_BLUR) {
    return {
        focused: false
    }
}
return state;
}
